                function formatTime(arg) {
                    //formats the time
                    var s = Math.round(arg);
                    var m = 0;
                    if (s > 0) {
                        while (s > 59 && s < 3000000 && isFinite(s)) {
                            m++;
                            s -= 60;
                        }
                        return String((m < 10 ? "0" : "") + m + ":" + (s < 10 ? "0" : "") + s);
                    } else {
                        return "00:00";
                    }
                }

                return this;
